LRESULT CMainFrame::OnFinishedTrainingChunk(WPARAM wParam, LPARAM lParam)
{
	//CNNDoc *pDoc = (CNNDoc *)GetActiveDocument(); 
	CNNDoc *pDoc = (CNNDoc *) (LPVOID) lParam; 
	// this should be replace sometimes with the document definition passed by the parameters
	// instead of the
#ifdef MY_DEBUG
	TRACE ("WmainFrame: finished training chunk\n");
#endif
	ASSERT_VALID(pDoc); //check that everything is dandy with the document befor calling it
	return pDoc->OnFinishedTrainingChunk(wParam,lParam);
	
}
